package com.day2;

public class AssignmentOperator {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a=10,b=10;
		
		if(a==b) {
			System.out.println("Two are equal");
		}
		int c=20;
		c+=10;
		System.out.println(c);
		int d=20;
		d-=10;
		System.out.println(d);
		int e=20;
		e*=10;
		System.out.println(e);

	}

}
